import React, { Component } from 'react';
import { Tabs } from '@tableau/tableau-ui'


const tabs = [ { content: 'Four' }, { content: 'Five' }, { content: 'Six' } ];
const initialState = { selectedTabIndex: 0 };

class TabsLeftAlign extends Component {

    state = { selectedTabIndex: 0 };

    render() {

        return(
            <>

            <br/>
            <Tabs
            activation='manual'
            alignment='left'
            onTabChange={(index) => {
                console.log(`onChange: ${index}`);
                setState({ selectedTabIndex: index });
            }}
            selectedTabIndex={state.selectedTabIndex}
            tabs={tabs}
            >
            <span>{tabs[state.selectedTabIndex].content} panel content</span>
            </Tabs>

            <br/>
            </>
        )
    }
}

export default TabsLeftAlign
