import React, { Component } from 'react';
import { Tabs } from '@tableau/tableau-ui'


const tabs = [ { content: 'Four' }, { content: 'Five' }, { content: 'Six' } ];

class TabsLeftAlign extends Component {

    componentWillMount() {
        this.state.selectedTabIndex = 0
    }

    render() {

        return(
            <>

            <br/>
            <Tabs
            activation='manual'
            alignment='left'
            onTabChange={(index) => {
                console.log(`onChange: ${index}`);
                this.setState({ selectedTabIndex: index });
            }}
            selectedTabIndex={this.state.selectedTabIndex}
            tabs={tabs}
            >
            <span>{tabs[this.state.selectedTabIndex].content} panel content</span>
            </Tabs>

            <br/>
            </>
        )
    }
}

export default TabsLeftAlign
